import React, {useEffect, useState} from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";
import {useLocation} from "react-router-dom";
import InfiniteScroll from "react-infinite-scroller";

const Adverts = (props) => {

  const location = useLocation().pathname;

  const [namePage, setNamePage] = useState('');
  const [page, setPage] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const [itemList, setItemList] = useState([]);

  useEffect(() => {
    switch (location) {
      case '/':
        setNamePage('Новые объявления');
        break;

      case '/transport':
        setNamePage('Транспорт');
        break;

      case '/nedvijimost':
        setNamePage('Недвижимость');
        break;

      case '/all_for_home':
        setNamePage('Всё для дома');
        break;

      case '/nedvijimost/kvartiri':
        setNamePage('Квартиры');
        break;

      case '/nedvijimost/doma':
        setNamePage('Квартиры')
        break;

      case '/transport/mashini':
        setNamePage('Машины');
        break;

      case '/transport/motocicly':
        setNamePage('Мотоциклы');
        break;

      case '/all_for_home/bitovaya_technika':
        setNamePage('Бытовая техника');
        break;

      case '/all_for_home/mebel':
        setNamePage('Мебель');
        break;

      default: setNamePage('');
    }
  });

  const handleLoadMore = () => {
    if (!isLoading) {
      setIsLoading(true);
      setHasMore(false);

      props.getAdverts({page, location});
      setPage(page + 1);
    }
  }

  useEffect(() => {
    setHasMore(itemList.length !== props.adverts.length);
    setItemList(getAdverts(props.adverts));
    setInterval(() => {
      setIsLoading(false);
    }, 1500);
  }, [props.adverts]);

  const getAdverts = (adverts) => {
    return adverts.map(ad => {
          return <Advert adId={ad.adId}
                         photos={ad.photos === null ? [] : ad.photos}
                         header={ad.header}
                         description={ad.description}
                         price={ad.price}
                         createdAt={ad.createdAt === null ? 'Нет информации' : ad.createdAt}/>
        }
    );
  }

  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>{namePage}</h4>
        </div>
        <InfiniteScroll
            pageStart={0}
            loadMore={handleLoadMore}
            hasMore={hasMore}
            loader={<div className={s.loader} key={0}>Loading ...</div>}>
            <div className={s.adverts}>
              {itemList}
            </div>
        </InfiniteScroll>
      </div>
  );
};

export default Adverts;
