import React, {useState} from 'react';

import s from './CreateAd.module.css'
import {getValue} from "@testing-library/user-event/dist/utils";
import Advert from "../Adverts/Advert/Advert";
import Navbar from "../Navbar/Navbar";
import {NavLink} from "react-router-dom";

const CreateAd = () => {

  const [selectCategory, setSelectCategory] = useState('');

  const handeSelectCategory = (event) => {
    setSelectCategory(event.target.value);
    if (event.target.value === 'Транспорт') {

    }

    if (event.target.value === 'Недвижимость') {

    }

    if (event.target.value === 'Все для дома') {

    }
  };

  return (
      <div className={s.content}>
        <h3 className={s.text}>Добавление объявления</h3>
        <div className={s.container}>
          <div className={s.contentForms}>
            <form className={s.form}>
              <div className={s.formContent}>
                <label className={s.label} htmlFor="category">Категория: </label>
                <select className={s.selector} value={selectCategory} onChange={(event => {handeSelectCategory(event)})} name="category" id="category">
                  <option value="Недвижимость">Недвижимость</option>
                  <option value="Транспорт">Транспорт</option>
                  <option value="Все для дома">Всё для дома</option>
                </select>
              </div>

              {selectCategory === 'Недвижимость' &&
                <div className={s.formContent}>
                  <label className={s.label} htmlFor="subcategory">Подкатегория: </label>
                  <select className={s.selector} value={selectCategory} onChange={(event => {handeSelectSubCategory(event)})} name="subcat" id="subcat">
                    <option value="Квартиры">Квартиры</option>
                    <option value="Дома">Дома</option>
                  </select>
                </div>}

              {selectCategory === 'Транспорт' &&
                <div className={s.formContent}>
                  <label className={s.label} htmlFor="subcategory">Подкатегория: </label>
                  <select className={s.selector} value={selectCategory} onChange={(event => {handeSelectSubCategory(event)})} name="subcat" id="subcat">
                    <option value="Машины">Машины</option>
                    <option value="Мотоциклы">Мотоциклы</option>
                  </select>
                </div>}

              {selectCategory === 'Все для дома' &&
                <div className={s.formContent}>
                  <label className={s.label} htmlFor="subcategory">Подкатегория: </label>
                  <select className={s.selector} value={selectCategory} onChange={(event => {handeSelectSubCategory(event)})} name="subcat" id="subcat">
                    <option value="Бытовая техника">Бытовая техника</option>
                    <option value="Мебель">Мебель</option>
                  </select>
                </div>}
            </form>
          </div>
        </div>
      </div>
  );
};

export default CreateAd;
