import React from 'react';
import {useLocation} from "react-router-dom";
import axios from "axios";

const OAuth = () => {
  const location = useLocation().hash;

  let accessToken;
  let expiresIn;
  let userId;
  let email;

  const handleOAuth = () => {
    let dataToken = location.match(/\w+=\w+/mg);

    if (location) {
      accessToken = dataToken[0].match(/=\w+/)[0].slice(1);
      expiresIn = dataToken[1].match(/=\w+/)[0].slice(1);
      userId = dataToken[2].match(/=\w+/)[0].slice(1);
      email = location.match(/\w+@\w+.\w+/)[0];
    }

    console.log(accessToken);

/*    let data = {
      user_ids: userId,
      fields: 'first_name, last_name',
      name_case: 'nom',
      access_token: accessToken,
      v: '5.131'
    }*/


    axios.get(`https://api.vk.com/method/users.get?user_ids=${userId}&access_token=${accessToken}&v=5.131`, {
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Access-Control-Allow-Headers": "*",
        "Access-Control-Allow-Methods": "*",
        'Access-Control-Allow-Credentials': 'true',
        'Content-Type': 'application/json',
      }
    }).then(
        response => console.log(response)

    )
  }

  return (
      <div>
        {handleOAuth()}
      </div>
  );
};

export default OAuth;
