import React from 'react';
import s from "./FileChooser.module.css";

const FileChooser = (props) => {
  const width = props.width === '' ? '100px' : `${props.width}px`;

  return (
      <div className={s.fchoose}>
        <span className={s.fchoose__label}>{props.list}</span>
        <input style={{width}} className={s.fchoose__input} ref={props.refer} type="file" accept={'.jpg, ,jpeg, .png'} onChange={props.handle}/>
        <button className={s.fchoose__btn}>Выбрать</button>
      </div>
  );
};

export default FileChooser;
