import React, {useState} from 'react';
import s from "./MainCharacteristic.module.css";
import Input from "../Input/Input";
import TextArea from "../TextArea/TextArea";

import store from "../../redux/redux-store";
import mainCharacteristicReducer from "../../redux/mainCharacteristicReducer";

const MainCharacteristic = () => {

  const onChangeDescription = (event) => {
    store.dispatch({type: 'CHANGE_DESCRIPTION_TEXT', text: event.target.value});
  }

  const onChangePrice = (event) => {
    store.dispatch( {type: 'CHANGE_PRICE_TEXT', text: event.target.value} )
  }

  let description = store.getState().mainCharacteristic.description;
  let price = store.getState().mainCharacteristic.price;
  let header = store.getState().mainCharacteristic.header;

  return (
      <div className={s.mainCharacteristic}>
        <Input name={'Название объявления '} value={header} handle={handleSetHeader}/>
        <Input name={'Цена (руб.) '} value={price} handle={onChangePrice}/>
        <TextArea name={'Описание '} value={description} handle={onChangeDescription}/>
      </div>
  );
};

export default MainCharacteristic;
