import axios from "axios";

const SET_VIEW_USER = 'SET_VIEW_USER';

let initialState = {
  user: {id: -1, username: 'Нет информации', }
}

const viewUserReducer = (state = initialState, action) => {
  switch (action.type) {

    case SET_VIEW_USER:
      return {
        ...state,
        user: action.user
      }

    default:
      return state;
  }
}

export const setUser = (user) => ({ type: SET_VIEW_USER, user });

export const getUser = (props) => {
  return (dispatch) => {
    axios.get(`user/id${props.id}`).then(
        response => dispatch(setUser(response.data))
    )
  }
}

export default viewUserReducer;
