export let findAdById = (id) => {
  for (let i = 0; i < state.listAd.length; i++) {
    if (state.listAd[i].adId == id) {
      return state.listAd[i];
    }
  }

  return null;
}

export let getCategoryAndSubCategoryBySubId = (id) => {
  let categoryId = -1;
  let subcategory = '';

  for (let i = 0; i < state.subCategories.length; i++) {
    for (let j = 0; j < state.subCategories[i].length; j++) {
      if (state.subCategories[i][j].id == id) {
        subcategory = state.subCategories[i][j].option;
        categoryId = i;
      }
    }
  }

  if (subcategory !== '')
    return {category: state.categories[categoryId].option, subcategory};

  return {category: 'Не найдена категория', subcategory: 'Не найдена подкатегория'};
}

