import React, {useState} from 'react';
import s from './Login.module.css'
import SpaceLine from "../../SpaceLine";

import gmail from './gmail.svg';
import facebook from './facebook.svg';
import vk from './vk.svg';
import AuthInput from "../AuthInput/AuthInput";

const Login = (props) => {
  const [login, setLogin] = useState('');
  const handleSetLogin = (event) => {
    setLogin(event.target.value);
  }

  return (
      <div className={s.login__outside}>
        <div className={s.background} onClick={props.handle}/>
        <div className={s.login}>
          <div className={s.login__content}>
            <AuthInput name={'Логин'} placeholder={'Имя пользователя или e-mail'} value={login} handle={handleSetLogin}/>
            <br/>
            <div className={s.login__item_input}>
              <span className={s.login__label}>Пароль</span>
              <input className={s.login__input} placeholder={'Введите Ваш пароль'} type="text"/>
            </div>
            <br/>
            <button className={s.btn}>Войти</button>
            <button className={`${s.btn} ${s.btnActive}`}>Регистрация</button>
            <span className={s.forgotPassword}>Забыли пароль?</span>
            <br/>
            <SpaceLine width={280} height={2}/>
            <span className={s.fastRegistration}>Быстрая регистрация</span>
            <div className={s.login__fastRegistration}>
              <img className={s.login__fastRegistration_item} src={gmail} alt="gmail"/>
              <img className={s.login__fastRegistration_item} src={facebook} alt="facebook"/>
              <img className={s.login__fastRegistration_item} src={vk} alt="vk"/>
            </div>
          </div>
        </div>
      </div>
  );
};

export default Login;
