import React, {useState} from 'react';
import s from "../CreateAd.module.css";

const OptionalSelector = (props) => {

  let optionList = props.optionalList.map(el => <option value={el.option} onChange={(event => {handleSelectItem(event)})}>{el.option}</option>);

  const [selectItem, setSelectItem] = useState('');
  const handleSelectItem = (event) => {
    console.log(event.target.value);
    setSelectItem(event.target.value);
  }

  return (
      <div className={s.formContent}>
        <label className={s.label}>{props.nameSelector}</label>
        <select className={s.selector} value={props.optionalList[0]}>
          {optionList}
        </select>
      </div>
  );
};

export default OptionalSelector;
