const SELECT_CATEGORY = 'SELECT_CATEGORY';
const SELECT_SUB_CATEGORY = 'SELECT_SUB_CATEGORY';

const CHANGE_HEADER_TEXT = 'CHANGE_HEADER_TEXT';
const CHANGE_PRICE_TEXT = 'CHANGE_PRICE_TEXT';
const CHANGE_DESCRIPTION_TEXT = 'CHANGE_DESCRIPTION_TEXT';

let initialState = {
  mainCharacteristic: {
    header: '',
    price: '',
    description: '',
  },
  categories: {
    category: 'Не выбрано',
    subcategory: 'Не выбрано'
  },
  immovables: {

  },
  transport: {

  },
  household: {

  }
}

const createAdReducer = (state = initialState, action) => {

  switch (action.type) {
    case SELECT_CATEGORY:
      state.categories.category = action.select;
      return state;
    case SELECT_SUB_CATEGORY:
      if (state.categories.subcategory !== 'Не выбрано')
        state.categories.subcategory = 'Не выбрано';
      state.categories.subcategory = action.select;
      return state;

    case CHANGE_HEADER_TEXT:
      state.mainCharacteristic.header = action.text;
      return state;
    case CHANGE_PRICE_TEXT:
      state.mainCharacteristic.price = action.text;
      return state;
    case CHANGE_DESCRIPTION_TEXT:
      state.mainCharacteristic.description = action.text;
      return state;



    default: return state;
  }
}

export const changeSelectCategory = (select) => ({ type: SELECT_CATEGORY, select })
export const changeSelectSubCategory = (select) => ({ type: SELECT_SUB_CATEGORY, select })

export const changeHeaderText = (text) => ({ type: CHANGE_HEADER_TEXT, text })
export const changePriceText = (text) => ({ type: CHANGE_PRICE_TEXT, text })
export const changeDescriptionText = (text) => ({ type: CHANGE_DESCRIPTION_TEXT, text })

export default createAdReducer;
