import React from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";

const Adverts = (props) => {

  const listAd = [
    {adId: 1, imgPath: 'kalina_4.jpg', header: 'Продам купола', price: '50.000', desc: 'Топ за свои деньги', datePublish: '30.03.2020', owner: 'FORTRIX'},
    {adId: 2, imgPath: 'kalina_4.jpg', header: 'Куплю купола', price: '30.000', desc: 'Топ за свои деньги', datePublish: '30.03.2020', owner: 'FORTRIX'},
    {adId: 3, imgPath: 'kalina_4.jpg', header: 'Xiaomi', price: '15000', desc: 'Топ за свои деньги', datePublish: '30.03.2020', owner: 'FORTRIX'},
    {adId: 4, imgPath: 'kalina_4.jpg', header: 'Куплю велосипед', price: '15000', desc: 'Топ за свои деньги', datePublish: '30.03.2020', owner: 'FORTRIX'},
  ]

  let advertsList = listAd.map(el => <Advert imgPath={el.imgPath} adName={el.header} price={el.price} description={el.desc} datePublish={el.datePublish}
    adId={el.adId} />);

  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>{props.categoryName}</h4>
        </div>
        <div className={s.adverts}>
          {advertsList}
          {advertsList}
          {advertsList}
          {advertsList}
          {advertsList}
        </div>
      </div>
  );
};

export default Adverts;
