import React, {useState} from 'react';
import Input from "../Input/Input";
import OptionalSelector from "../OptionalSelector/OptionalSelector";
import SpaceLine from "../../SpaceLine";

import s from "./Characteristics.module.css";

const Characteristics = (props) => {

  const [rent, setRent] = useState('Не выбрано');
  const handleSetRent = (event) => {
    if (event.target.value !== 'Не выбрано')
      setRentPeriod('Не выбрано');

    setRent(event.target.value);
  }

  const [rentPeriod, setRentPeriod] = useState('Не выбрано');
  const handleSetRentPeriod = (event) => {
    setRentPeriod(event.target.value);
  }

  const [roomNumber, setRoomNumber] = useState('');
  const handleSetRoomNumber = (event) => {
    setRoomNumber(event.target.value);
  }

  const [stage, setStage] = useState('');
  const handleSetStage = (event) => {
    setStage(event.target.value);
  }


  const optionalListBool = [{id: 0, option: 'Да'}, {id: 1, option: 'Нет'}]
  const optionalListRent = [{id: 0, option: 'Длительный'}, {id: 1, option: 'Посуточный'}]

  let listItemsToOptions = (listItems) => {
    return listItems.map(el => <option key={el.id} value={el.option}>{el.option}</option>);
  };

  return (
      <div className={s.characteristics}>
        <SpaceLine/>

        {props.type === 'Квартиры' && <div>
          <OptionalSelector label={'Аренда'} value={rent} handle={handleSetRent} options={listItemsToOptions(optionalListBool)}/>
          {rent === 'Да' && <OptionalSelector label={'Срок аренды'} value={rentPeriod} handle={handleSetRentPeriod} options={listItemsToOptions(optionalListRent)}/>}
          <Input name={'Количество комнат'} value={roomNumber} handle={handleSetRoomNumber}/>
          <Input name={'Этаж'} value={stage} handle={handleSetStage}/>
        </div>}
        {props.type === 'Дома' && <div>
          1
        </div>}
        {props.type === 'Машины' && <div>
          2
        </div>}
        {props.type === 'Мотоциклы' && <div>
          3
        </div>}
        {props.type === 'Бытовая техника' && <div>
          4
        </div>}
        {props.type === 'Мебель' && <div>
          5
        </div>}
        <button><NavLink to={'/'}>На главную</NavLink></button>
      </div>
  );
};

export default Characteristics;
