import React, {useState} from 'react';
import s from "./MainCharacteristic.module.css";
import Input from "../Input/Input";
import TextArea from "../TextArea/TextArea";

import store from "../../redux/redux-store";
import {
  changeDescription,
  changeHeader,
  changePrice,
} from "../../redux/CreateAdReducer";

const MainCharacteristic = () => {

  const onChangeDescription = (event) => {
    store.dispatch(changeDescription(event.target.value));
  }

  const onChangePrice = (event) => {
    store.dispatch(changePrice(event.target.value));
  }

  const onChangeHeader = (event) => {
    store.dispatch(changeHeader(event.target.value))
  }

  let description = store.getState().create.mainCharacteristic.description;
  let price = store.getState().create.mainCharacteristic.price;
  let header = store.getState().create.mainCharacteristic.header;

  return (
      <div className={s.mainCharacteristic}>
        <Input name={'Название объявления '} value={header} handle={onChangeHeader}/>
        <Input name={'Цена (руб.) '} value={price} handle={onChangePrice}/>
        <TextArea name={'Описание '} value={description} handle={onChangeDescription}/>
      </div>
  );
};

export default MainCharacteristic;
