import React, {useEffect, useState} from 'react';
import s from './Adverts.module.css';

import {useLocation} from "react-router-dom";
import InfiniteScroll from "react-infinite-scroller";
import mapAdListToAdJSX from "../mapAdListToAdJSX";

const Adverts = (props) => {

  const location = useLocation();
  const decodeSearch = decodeURI(location.search).toString();
  const search = decodeSearch.slice(1);

  console.log(search);

  const [namePage, setNamePage] = useState('');
  const [page, setPage] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const [itemList, setItemList] = useState([]);

  useEffect(() => {

    if (props.namePage == '')
      props.setNamePage(location);

    if (props.namePage)
      setNamePage(props.namePage);

  }, [props.namePage]);

  const handleLoadMore = () => {
    if (!isLoading) {
      setIsLoading(true);
      setHasMore(false);

      props.getAdverts({page, location});
      setPage(page + 1);
    }
  }

  useEffect(() => {
    setHasMore(itemList.length !== props.adverts.length);
    setItemList(mapAdListToAdJSX(props.adverts));
    setInterval(() => {
      setIsLoading(false);
    }, 1500);
  }, [props.adverts]);

  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>{namePage}</h4>
        </div>
        <InfiniteScroll
            pageStart={0}
            loadMore={handleLoadMore}
            hasMore={hasMore}
            loader={<div className={s.loader} key={0}>Loading ...</div>}>
            <div className={s.adverts}>
              {itemList}
            </div>
        </InfiniteScroll>
      </div>
  );
};

export default Adverts;
