import React from 'react';
import {useParams} from "react-router-dom";

import s from './ViewAd.module.css';
import img from './exmpl.png';

const ViewAd = (props) => {
  const params = useParams();
  const advert = props.findAdById(params.id);
  const categoryAndSubCategory = props.getCategoryAndSubCategoryBySubId(advert.subId);

  const user = props.findUserByUsername(advert.owner);
  const userAvatarPath = `/avatars/${user.id}.jpg`;

  return (
      <div className={s.ad}>
        <span className={s.ad__path}> Главная > {categoryAndSubCategory.category} > <span className={s.ad__path_endpoint}>{categoryAndSubCategory.subcategory}</span> </span>
        <div className={s.ad__window}>
          <div className={s.ad__leftContent}>
            <span className={s.ad__header}>{advert.header}</span>
            <span className={s.ad__datePublish}>{advert.datePublish}</span>
            <div className={s.ad__images}>
              <div className={s.ad__images_mainImg} style={{backgroundImage: `url("${img}")`}}/>
              <div className={s.ad__images_otherImg}>
                <div className={s.ad__images_secondaryImg} style={{backgroundImage: `url("${img}")`}}/>
                <div className={s.ad__images_secondaryImg} style={{backgroundImage: `url("${img}")`}}/>
                <div className={s.ad__images_secondaryImg} style={{backgroundImage: `url("${img}")`}}/>
                <div className={s.ad__images_secondaryImg} style={{backgroundImage: `url("${img}")`}}/>
                <div className={s.ad__images_secondaryImg} style={{backgroundImage: `url("${img}")`}}/>
                <div className={s.ad__images_secondaryImg} style={{backgroundImage: `url("${img}")`}}/>
              </div>
            </div>

            <span className={s.ad__description_header}>Описание</span>
            <span className={s.ad__description_text}>{advert.desc}</span>
          </div>
          <div className={s.ad__rightContent}>
            <span className={s.ad__price}>{advert.price} ₽</span>
            <button className={s.ad__btn}>Показать номер</button>
            <button className={s.ad__activeBtn}>Написать</button>
            <img className={s.ad__userAvatar} style={{backgroundImage: `url("${userAvatarPath}")`}} alt={user.username}/>
          </div>
        </div>
      </div>
  );
};

export default ViewAd;
