import React, {useEffect, useState} from 'react';

import s from './CreateAd.module.css'
import OptionalSelector from "./OptionalSelector/OptionalSelector";
import SpaceLine from "../SpaceLine";
import MainCharacteristic from "./MainCharacteristic/MainCharacteristic";
import Characteristics from "./Characteristics/Characteristics";

const CreateAd = (props) => {

  console.log(props)

  let optionCategories = [{id: 1, option: 'Недвижимость'},
    {id: 2, option: 'Транспорт'},
    {id: 3, option: 'Всё для дома'}];
  let optionSubCategories = [{id: 1, option: 'Недвижимость'},
      {id: 2, option: 'Транспорт'},
      {id: 3, option: 'Всё для дома'}]

  let selectCategory = props.categories.category;
  let selectSubCategory = props.categories.subcategory;

  useEffect(() => {
    props.getCategories();
    console.log(props.categoriesOption);
  }, []);

  let getSelectedCategory = () => {
    switch (selectCategory) {
      case 'Недвижимость':
        return 0;
        break;
      case 'Транспорт':
        return 1;
        break;
      case 'Всё для дома':
        return 2;
        break;
      default:
        return -1;
    }
  }

  let listItemsToOptions = (listItems) => {
    return listItems.map(el => <option key={el.id} value={el.option}>{el.option}</option>);
  }

  return (
      <div className={s.content}>
        <h3 className={s.text}>Добавление объявления</h3>
        <div className={s.container}>
          <div className={s.contentForms}>
            <form className={s.form}>
              <OptionalSelector label={'Категория '} options={listItemsToOptions(optionCategories)}
                                value={selectCategory}/>
              {selectCategory !== 'Не выбрано' &&
                  <OptionalSelector label={'Подкатегория '} options={listItemsToOptions(optionSubCategories)}
                                    value={selectSubCategory}/>
              }
              <SpaceLine width={600}/>
              <MainCharacteristic store={props}/>

              {/*<Characteristics type={selectSubCategory} optional={props.optional}/>*/}
            </form>
          </div>
        </div>
      </div>
  );
};

export default CreateAd;
