import axios from "axios";
import {
  isContentLoaded,
  setAppartaments,
  setAppliance,
  setCars,
  setFurniture, setHousehold,
  setHouses, setImmovables,
  setMoto, setTransport
} from "../Reducers/UrlReducer";

const setAllSubcategories = (data) => {
  debugger;
  setHouses(data[0]);
  setAppartaments(data[1]);

  setCars(data[2]);
  setMoto(data[3]);

  setAppliance(data[4]);
  setFurniture(data[5]);
}

const setAllCategories = (data) => {
  setTransport(data[0]);
  setHousehold(data[1]);
  setImmovables(data[2]);
}

export const getSubcategories = () => {
  return (dispatch) => {
    axios.get('http://localhost:8080/subcategory/').then(
        response => {
          setAllSubcategories(response.data);
          dispatch(isContentLoaded());
        }
    )
  }
}

export const getCategories = () => {
  return (dispatch) => {
    axios.get('http://localhost:8080/category/').then(
        response => {
          setAllCategories(response.data);
          dispatch(isContentLoaded());
        }
    )
  }
}
