import React, {useState} from 'react';
import s from './Navbar.module.css'
import {NavLink} from "react-router-dom";

import Login from "../Authentication/Login/Login";
import Registration from "../Authentication/Registration/Registration";
import ForgotPassword from "../Authentication/ForgotPassword/ForgotPassword";
import LoginContainer from "../Authentication/Login/LoginContainer";

const Navbar = (props) => {

  debugger;

  const [request, setRequest] = useState('');
  const handleSetRequest = (value) => {
    setRequest(value);
  }

  const [toggleLogin, setToggleLogin] = useState(false);
  const handleSetToggleLogin = () => {
    setToggleLogin(!toggleLogin);
  }

  const [toggleRegistration, setToggleRegistration] = useState(false);
  const handleSetToggleRegistration = () => {
    setToggleLogin(false);
    setToggleRegistration(!toggleRegistration);
  }

  const [toggleForgorPassword, setForgotPassword] = useState(false);
  const handleSetForgorPassword = () => {
    setToggleLogin(false);
    setForgotPassword(!toggleForgorPassword);
  }

  return (
        <navbar className={s.navbar}>
          {toggleLogin === true && <LoginContainer handleLog={handleSetToggleLogin} handleReg={handleSetToggleRegistration} handleForgot={handleSetForgorPassword}/>}
          {toggleRegistration === true && <Registration handle={handleSetToggleRegistration}/>}
          {toggleForgorPassword === true && <ForgotPassword handle={handleSetForgorPassword}/>}
          <NavLink className={s.logo} to={"/"}><span className={s.logoLetterE}>e</span>-board</NavLink>
          <input className={s.input} type="text" placeholder={'Поиск'} value={request} onChange={event => handleSetRequest(event.target.value)}/>
          <NavLink className={s.btnLink} to={`/ad/search/?=${request}`}><button className={`${s.btn} ${s.button}`}>Поиск</button></NavLink>
          <NavLink className={s.btnLink} to={'/ad/create'}><button className={`${s.btn} ${s.button}`}>Разместить объявление</button></NavLink>
          <button className={`${s.btn} ${s.buttonActive}`} onClick={handleSetToggleLogin}>Войти</button>
        </navbar>
  );
};

export default Navbar;
