import React from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";

import {BrowserRouter, Router, Routes, Route, Switch} from "react-router-dom";

const Adverts = (props) => {

  const listAd = [
    {
      adId: 1,
      imgPath: "kalina_4.jpg",
      adName: "Продам купола",
      price: 50.000,
      desc: "Топ за свои деньги",
      datePublish: "30.03.2020"
    },
    {
      adId: 2,
      imgPath: "kalina_4.jpg",
      adName: "Куплю купола",
      price: 30.000,
      desc: "Топ за свои деньги",
      datePublish: "30.03.2020"
    },
    {
      adId: 3,
      imgPath: "kalina_4.jpg",
      adName: "Xiaomi",
      price: 15000,
      desc: "Топ за свои деньги",
      datePublish: "30.03.2020"
    },
    {
      adId: 4,
      imgPath: "kalina_4.jpg",
      adName: "Куплю велосипед",
      price: 15000,
      desc: "Топ за свои деньги",
      datePublish: "30.03.2020"
    },
  ]

  let advertsList = listAd.map(el => {
    <Advert imgPath={el.imgPath} adName={el.adName} price={el.price} description={el.desc} datePublish={el.datePublish}
            adId={el.adId}/> });

  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>{props.categoryName}</h4>
        </div>
        <div className={s.adverts}>
          {advertsList.length}
        </div>
      </div>
  );
};

export default Adverts;
