import React from 'react';
import {useLocation} from "react-router-dom";
import axios from "axios";

const OAuth = () => {
  const location = useLocation().hash;

  const handleOAuth = () => {
    let dataToken = location.match(/\w+=\w+/mg);

    const accessToken = dataToken[0].match(/=\w+/)[0].slice(1);
    const expiresIn = dataToken[1].match(/=\w+/)[0].slice(1);
    const userId = dataToken[2].match(/=\w+/)[0].slice(1);
    const email = location.match(/\w+@\w+.\w+/)[0];

    console.log(accessToken);

    let config = {
      user_ids: userId,
      fields: 'first_name, last_name',
      name_case: 'nom',
      access_token: accessToken,
      v: '5.131'
    }

    axios.post(`https://api.vk.com/method/users.get`, {})
  }

  return (handleOAuth());
};

export default OAuth;
