import React from 'react';
import s from "./MainCharacteristic.module.css";
import Input from "../Input/Input";
import TextArea from "../TextArea/TextArea";

const MainCharacteristic = () => {
  return (
      <div className={s.mainCharacteristic}>
        <Input name={'Название объявления '} value={header} onChange={(event => setHeader(event.target.value))}/>
        <Input name={'Цена (руб.) '} value={price} onChange={(event => setPrice(event.target.value))}/>
        <TextArea name={'Описание '} value={description} onChange={(event => setDescription(event.target.value))}/>
      </div>
  );
};

export default MainCharacteristic;
