import React, {useEffect, useState} from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";
import {useLocation} from "react-router-dom";
import InfiniteScroll from "react-infinite-scroller";

const Adverts = (props) => {

/*  let advertsList = props.listAd.map(el => <Advert imgPath={el.imgPath} adName={el.header} price={el.price} description={el.desc} datePublish={el.datePublish}
    adId={el.adId} />);*/

  const location = useLocation().pathname;

  let [adverts, setAdverts] = useState([]);
  const [itemList, setItemList] = useState([]);

  const [page, setPage] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [hasMore, setHaseMore] = useState(true);

  const loadMore = () => {
    if (!isLoading)
      handleLoadMore();
  }

  const handleLoadMore = () => {

    switch (location) {
      case '/':
        props.getNewAds(page);
        setAdverts(props.newAds);
        break;

      case '/nedvijimost':
        props.getImmovables(page);
        setAdverts(props.immovables);
        break;

      case '/transport':
        props.getTransport(page);
        setAdverts(props.transport);
        break;

      default:
        return;
    }

  };

  useEffect(() => {
    if (!isLoading) {
      setIsLoading(!isLoading);
      setHaseMore(false);
      setPage(page + 1);
    }

    if (adverts.length > 0) {
      setItemList(getAdverts(adverts));
      setHaseMore(true);
      setIsLoading(false);
      debugger;
    }
  }, [adverts, hasMore, isLoading])

  const getAdverts = (adverts) => {
    return adverts.map(ad => {
          return <Advert adId={ad.adId}
                         photos={ad.photos === null ? [] : ad.photos}
                         header={ad.header}
                         description={ad.description}
                         price={ad.price}
                         createdAt={ad.createdAt === null ? 'Нет информации' : ad.createdAt}/>
        }
    );
  }

  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>Категория</h4>
        </div>
        <InfiniteScroll
            pageStart={0}
            loadMore={loadMore}
            hasMore={hasMore}
            loader={<div className={s.loader} key={0}>Loading ...</div>}>
            <div className={s.adverts}>
              {itemList}
            </div>
        </InfiniteScroll>
      </div>
  );
};

export default Adverts;
