import React, {useEffect, useState} from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";
import {useLocation} from "react-router-dom";
import InfiniteScroll from "react-infinite-scroller";

const Adverts = (props) => {

/*  let advertsList = props.listAd.map(el => <Advert imgPath={el.imgPath} adName={el.header} price={el.price} description={el.desc} datePublish={el.datePublish}
    adId={el.adId} />);*/

  const location = useLocation().pathname;

  let adverts = [];
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);

  const loadMore = async () => {
    setPage(page + 1);
    switch (location) {
      case '/':
        setHasMore(false);
        await props.getNewAds(page);
        adverts = props.newAds;
        break;

      case '/nedvijimost':
        setHasMore(false);
        await props.getImmovables(page);
        adverts = props.immovables;
        break;

      case '/transport':
        setHasMore(false);
        await props.getTransport(page);
        adverts = props.transport;
        break;

      default: return;
    }
  }

  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>Категория</h4>
        </div>
        <div className={s.adverts}>
{/*          <InfiniteScroll
              pageStart={0}
              loadMore={loadMore}
              hasMore={hasMore}
              loader={<div className={s.loader} key={0}>Loading ...</div>}>
              {advertsList}
          </InfiniteScroll>*/}
        </div>

      </div>
  );
};

export default Adverts;
