import axios from "axios";
import characteristics from "../CreateAd/Characteristics/Characteristics";

const SET_ADVERT = 'SET_ADVERT';
const SET_CHARACTERISTIC = 'SET_CHARACTERISTIC';
const SET_CATEGORY = 'SET_CATEGORY';
const SET_OWNER = 'SET_OWNER';
const SET_SUBCATEGORY = 'SET_SUBCATEGORY';

let initialState = {
  advert: {photos: null},
  characteristic: {id: -1, name: 'неизвестно'},
  category: {id: -1, categoryName: 'неизвестно'},
  subcategory: {id: -1, subcategories: 'неизвестно'},
  owner: {id: -1, name: 'неизвестно', avatar: null},
}

const viewAdReducer = (state = initialState, action) => {
  debugger;

  switch (action.type) {
    case SET_ADVERT:
      getCharacteristic(action.advert.adId);
      getCategory(action.advert.subId);
      getOwner(action.advert.owner);
      debugger;
      return {
        ...state,
        advert: action.advert
      }

    case SET_CHARACTERISTIC:
      return {
        ...state,
        characteristic: action.characteristics.characteristic
      }

    case SET_CATEGORY:
      return {
        ...state,
        category: action.category
      }

    case SET_OWNER:
      return {
        ...state,
        owner: action.owner
      }

    case SET_SUBCATEGORY:
      return {
        ...state,
        subcategory: action.subId
      }

    default:
      return state;
  }
}

export const setAdvert = (advert) => ({ type: SET_ADVERT, advert });
export const setCharacteristic = (characteristics) => ({ type: SET_CHARACTERISTIC, characteristics });
export const setCategory = (category) => ({ type: SET_CATEGORY, category });
export const setOwner = (owner) => ({ type: SET_OWNER, owner });
export const setSubcategory = (subId) => ({ type: SET_SUBCATEGORY, subId });

export const getAdvert = (props) => {
  return (dispatch) => {
    axios.get(`/ad/view/id${props}`).then(
        response => {
          dispatch(setAdvert(response.data));
        }
    ).catch(
      reason => {
        dispatch(setAdvert(reason));
      }
    )
  }
}

export const getCharacteristic = (props) => {
  return (dispatch) => {
    axios.get(`/characteristic/advert/id${props}`).then(
        response => {
          dispatch(setCharacteristic(response.data));
        }
    ).catch(
        reason => {
          dispatch(setCharacteristic(reason));
        }
    )
  }
}

export const getCategory = (props) => {
  return (dispatch) => {
    axios.get(`/category/subcategory/id${props}`).then(
        response => {
          dispatch(setCategory(response.data));
        }
    ).catch(
        reason => {
          dispatch(setCategory(reason));
        }
    )
  }
}

export const getSubcategory = (props) => {
  return (dispatch) => {
    axios.get(`/subcategory/id${props}`).then(
        response => {
          dispatch(setSubcategory(response.data));
        }
    ).catch(
        reason => {
          dispatch(setSubcategory(reason));
        }
    )
  }
}

export const getOwner = (props) => {
  return (dispatch) => {
    axios.get(`/user/${props}`).then(
        response => {
          dispatch(setOwner(response.data));
        }
    ).catch(
        reason => {
          dispatch(setOwner(reason));
        }
    )
  }
}

export default viewAdReducer;
