import axios from "axios";

export const MAIN_PAGE = {url: '/', name: 'Новые объявления'};

export const TRANSPORT = {url: '/transport', name: 'Транспорт'};
export const IMMOVABLES = {url: '/nedvijimost', name: 'Недвижимость'};
export const HOUSEHOLD = {url: '/vse_dlya_doma', name: 'Всё для дома'};

export const APPARTAMENTS = {url: '/nedvijimost/kvartiri', name: 'Квартиры'};
export const HOUSES = {url: '/nedvijimost/doma', name: 'Дома'};

export const CARS = {url: '/transport/mashini', name: 'Машины'};
export const MOTO = {url: '/transport/motocicly', name: 'Мотоциклы'};

export const APPLIANCE = {url: '/vse_dlya_doma/bitovaya_technika', name: 'Бытовая техника'};
export const FURNITURE = {url: '/vse_dlya_doma/mebel', name: 'Мебель'};

export const AD_SEARCH = {url: '/ad/search/', name: 'Поиск по объявлениям'}

const SET_TRANSPORT = 'SET_TRANSPORT';
const SET_IMMOVABLES = 'SET_IMMOVABLES';
const SET_HOUSEHOLD = 'SET_HOUSEHOLD';

const SET_APPARTAMENTS = 'SET_APPARTAMENTS';
const SET_HOUSES = 'SET_HOUSES';

const SET_CARS = 'SET_CARS';
const SET_MOTO = 'SET_MOTO';

const SET_APPLIANCE = 'SET_APPLIANCE';
const SET_FURNITURE = 'SET_FURNITURE';

const CHECK_CONTENT = 'CHECK_CONTENT';

const CURRENT_CATEGORY = 'CURRENT_CATEGORY';

let initialState = {

  mainPage: {url: '/', name: 'Новые объявления'},
  adSearch: {url: '/ad/search/', name: 'Поиск по объявлениям'},

  transport: null,
  immovables: null,
  household: null,

  appartaments: null,
  houses: null,

  cars: null,
  moto: null,

  appliance: null,
  furniture: null,

  isContentLoaded: false,

  currentCategory: null,
}

const urlReducer = (state = initialState, action) => {

  switch (action.type) {

    case CURRENT_CATEGORY:
      switch (action.location) {

        case '/':
          return {...state, currentCategory: mainPage}

        case '/ad/search/':
          return {...state, currentCategory: adSearch}

        case state.transport.categoryUrl:
          return {...state, currentCategory: state.transport}

        case state.immovables.categoryUrl:
          return {...state, currentCategory: state.immovables}

        case state.household.categoryUrl:
          return {...state, currentCategory: state.household}

        case state.appartaments.subcategoryUrl:
          return {...state, currentCategory: state.appartaments}

        case state.houses.subcategoryUrl:
          return {...state, currentCategory: state.houses}

        case state.cars.subcategoryUrl:
          return {...state, currentCategory: state.cars}

        case state.moto.subcategoryUrl:
          return {...state, currentCategory: state.moto}

        case state.appliance.subcategoryUrl:
          return {...state, currentCategory: state.appliance}

        case state.furniture.subcategoryUrl:
          return {...state, currentCategory: state.furniture}

        default: return null;
      }

    case SET_TRANSPORT:
      return {
        ...state,
        transport: action.data
      }

    case SET_IMMOVABLES:
      return {
        ...state,
        immovables: action.data
      }

    case SET_HOUSEHOLD:
      return {
        ...state,
        household: action.data
      }

    case SET_APPARTAMENTS:
      return {
        ...state,
        appartaments: action.data
      }

    case SET_HOUSES:
      return {
        ...state,
        houses: action.data
      }

    case SET_CARS:
      return {
        ...state,
        cars: action.data
      }

    case SET_MOTO:
      return {
        ...state,
        moto: action.data
      }

    case SET_APPLIANCE:
      return {
        ...state,
        appliance: action.data
      }

    case SET_FURNITURE:
      return {
        ...state,
        furniture: action.data
      }

    case CHECK_CONTENT:
      return {
        ...state,
        isContentLoaded: !(state.transport == null || state.immovables == null || state.household == null ||
            state.appartaments == null || state.houses == null || state.cars == null || state.moto == null ||
            state.appliance == null || state.furniture == null)
      }

    default: return state;
  }
}

const setTransport = (data) => ({ type: SET_TRANSPORT, data });
const setImmovables = (data) => ({ type: SET_IMMOVABLES, data });
const setHousehold = (data) => ({ type:SET_HOUSEHOLD, data });

const setAppartaments = (data) => ({ type: SET_APPARTAMENTS, data });
const setHouses = (data) => ({ type: SET_HOUSES, data });

const setCars = (data) => ({ type: SET_CARS, data });
const setMoto = (data) => ({ type: SET_MOTO, data });

const setAppliance = (data) => ({ type: SET_APPLIANCE, data });
const setFurniture = (data) => ({ type: SET_FURNITURE, data });

const isContentLoaded = () => ({ type: CHECK_CONTENT });

export const getCurrentCategory = (location) => ({ type: CURRENT_CATEGORY, location });

export const getSubcategories = () => {
  return (dispatch) => {
    axios.get('/subcategory/').then(
        response => {
          dispatch(setHouses(response.data[0]));
          dispatch(setAppartaments(response.data[1]));

          dispatch(setCars(response.data[2]));
          dispatch(setMoto(response.data[3]));

          dispatch(setAppliance(response.data[4]));
          dispatch(setFurniture(response.data[5]));
          dispatch(isContentLoaded());
        }
    )
  }
}

export const getCategories = () => {
  return (dispatch) => {
    axios.get('/category/').then(
        response => {
          dispatch(setTransport(response.data[0]));
          dispatch(setHousehold(response.data[1]));
          dispatch(setImmovables(response.data[2]));
          dispatch(isContentLoaded());
        }
    )
  }
}

export default urlReducer;
