import React, {useEffect, useState} from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";
import {useLocation} from "react-router-dom";
import InfiniteScroll from "react-infinite-scroller";

const Adverts = (props) => {

/*  let advertsList = props.listAd.map(el => <Advert imgPath={el.imgPath} adName={el.header} price={el.price} description={el.desc} datePublish={el.datePublish}
    adId={el.adId} />);*/

  const location = useLocation().pathname;

  let adverts = [];
  const [itemList, setItemList] = useState([]);

  const [page, setPage] = useState(0);
  const [isLoading, setIsLoading] = useState(false);

  const loadMore = () => {
    if (isLoading === false) {
      setIsLoading(true);

    }
  }

  useEffect( () => {
    setPage(page + 1);

    if (isLoading) {
      switch (location) {
        case '/':
          props.getNewAds(page);
          adverts = props.newAds;
          break;

        case '/nedvijimost':
          props.getImmovables(page);
          adverts = props.immovables;
          break;

        case '/transport':
          props.getTransport(page);
          adverts = props.transport;
          break;

        default:
          return;
      }
    }
    if (adverts.length > 0)
      setItemList(getAdverts(adverts));

  }, [isLoading, props]);

  const getAdverts = (adverts) => {
    return adverts.map(ad => {
          return <Advert adId={ad.adId}
                         photos={ad.photos === null ? [] : ad.photos}
                         header={ad.header}
                         description={ad.description}
                         price={ad.price}
                         createdAt={ad.createdAt === null ? 'Нет информации' : ad.createdAt}/>
        }
    );
  }

  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>Категория</h4>
        </div>
        <InfiniteScroll
            pageStart={0}
            loadMore={loadMore}
            hasMore={false}
            loader={<div className={s.loader} key={0}>Loading ...</div>}>
            <div className={s.adverts}>
              {itemList}
            </div>
        </InfiniteScroll>
      </div>
  );
};

export default Adverts;
