import React, {useEffect, useState} from 'react';
import s from './SearchFilter.module.css';

const SearchFilter = (props) => {

  // props.currentCategory.name - запрашиваем category и subcategory
  // props.currentCategory.categoryName - запрашиваем subcategory
  // props.currentCategory.subcategories - запрашиваем сразу список характеристик
  const [categoryId, setCategoryId] = useState(0);
  const handleSetCategoryId = (id) => {
    setCategoryId(id);
  }

  const [chosedCategory, setChosedCategory] = useState('Не выбрано');
  const handleSetChosedCategory = (category) => {
    handleSetCategoryId(category);
    setChosedCategory(category);
  }


  const [subcategoryId, setSubcategoryId] = useState(0);
  const handleSetSubcategoryId = (id) => {
    setSubcategoryId(id);
  }

  const [chosedSubcategory, setChosedSubcategory] = useState('Не выбрано');
  const handleSetChosedSubcategory = (subcategory) => {
    handleSetSubcategoryId(subcategory);
    setChosedSubcategory(subcategory);
  }

  const getSubcategoryListByCurrentCategory = () => {
    let subcategoryList = [];

    if (props.subcategoryList.length > 0) {
      for (let i = 0; i < props.subcategoryList.length; i++) {
        debugger;
        if (props.subcategoryList[i].categoryId === categoryId) {
          subcategoryList.push(props.subcategoryList[i]);
        }
      }
    }

    return subcategoryList;
  }

  const getCategoryOptions = () => {
    return props.categoryList.map(category => {
      return <option value={category.id}>{category.option}</option>
    })
  };

  const getSubcategoryOptions = () => {
    const subcategoryList = getSubcategoryListByCurrentCategory();

    return subcategoryList.map(subcategory => {
      return <option value={subcategory.id}>{subcategory.option}</option>
    })
  };

  useEffect(() => {
    if (props.subcategoryList.length === 0) {
      if (props.currentCategory.name) {
        props.getSubCategories();
        props.getCategories();
      }

      if (props.currentCategory.categoryName)
        props.getSubCategories();
    }
  }, [props.subcategoryList]);

  // создать компоненты input,
  return (
      <div className={s.filter__container}>
        {props.currentCategory.name &&
          <div>
            <select value={chosedCategory} onChange={event => handleSetChosedCategory(event.target.value)}>
              <option disabled hidden>Не выбрано</option>
              {getCategoryOptions()}
            </select>

            <select value={chosedSubcategory} onChange={event => handleSetChosedSubcategory(event.target.value)}>
              <option disabled hidden>Не выбрано</option>
              {getSubcategoryOptions()}
            </select>
          </div>
        }

        {props.currentCategory.categoryName &&
          <div>

          </div>
        }

        {(props.currentCategory.subcategories || subcategoryId > 0) &&
          <div>

          </div>
        }
      </div>
  );
};

export default SearchFilter;
