import React, {useState} from 'react';

import s from './CreateAd.module.css'
import {getValue} from "@testing-library/user-event/dist/utils";
import Advert from "../Adverts/Advert/Advert";
import Navbar from "../Navbar/Navbar";
import {NavLink} from "react-router-dom";
import OptionalSelector from "./OptionalSelector/OptionalSelector";

const CreateAd = () => {

  const [selectCategory, setSelectCategory] = useState('');
  const handleSelectCategory = (event) => {
    console.log(event.target.value);
    setSelectCategory(event.target.value);
  }

  const [selectSubCategory, setSelectSubCategory] = useState('');
  const handleSelectSubCategory = (event) => {
    setSelectSubCategory(event.target.value);
  }

  let categoriesList = [
    {option: 'Недвижимость'},
    {option: 'Транспорт'},
    {option: 'Всё для дома'}
  ];
  let categoriesOptions = categoriesList.map(el => <option value={el.option} onChange={(event => {handleSelectCategory(event)})}>{el.option}</option>)

  let subCatList = [
      [
        {option: 'Квартиры'},
        {option: 'Дома'},
      ],
      [
        {option: 'Машины'},
        {option: 'Мотоциклы'},
      ],
      [
        {option: 'Бытовая техника'},
        {option: 'Мебель'},
      ]
  ];


  const SelectSubCat = () => {
    if (selectCategory === 'Недвижимость') {
      return <div className={s.formContent}>
        <label className={s.label} htmlFor="subcategory">Подкатегория: </label>
        <select className={s.selector} value={selectSubCategory} onChange={(event => {handleSelectSubCategory(event)})} name="subcat" id="subcat">
          <option value="Квартиры">Квартиры</option>
          <option value="Дома">Дома</option>
        </select>
      </div>
    }

    if (selectCategory === 'Транспорт') {
      return <div className={s.formContent}>
        <label className={s.label} htmlFor="subcategory">Подкатегория: </label>
        <select className={s.selector} value={selectSubCategory} onChange={(event => {handleSelectSubCategory(event)})} name="subcat" id="subcat">
          <option value="Машины">Машины</option>
          <option value="Мотоциклы">Мотоциклы</option>
        </select>
      </div>
    }

    if (selectCategory === 'Всё для дома') {
      return <div className={s.formContent}>
        <label className={s.label} htmlFor="subcategory">Подкатегория: </label>
        <select className={s.selector} value={selectSubCategory} onChange={(event => {handleSelectSubCategory(event)})} name="subcat" id="subcat">
          <option value="Бытовая техника">Бытовая техника</option>
          <option value="Мебель">Мебель</option>
        </select>
      </div>
    }

    return <div/>;
  }

  const OptionalSelector = (props) => {
    <div className={s.formContent}>
      <label className={s.label} htmlFor="category">Категория: </label>
      <select className={s.selector} value={selectCategory} onChange={(event => {handleSelectCategory(event)})} name="category" id="category">
        {categoriesOptions}
      </select>
    </div>
  }

  return (
      <div className={s.content}>
        <h3 className={s.text}>Добавление объявления</h3>
        <div className={s.container}>
          <div className={s.contentForms}>
            <form className={s.form}>

              {/*<div className={s.formContent}>
                <label className={s.label} htmlFor="category">Категория: </label>
                <select className={s.selector} value={selectCategory} onChange={(event => {handleSelectCategory(event)})} name="category" id="category">
                  {categoriesOptions}
                </select>
              </div>*/}

              <OptionalSelector label={'Категория: '} options={categoriesOptions} handle={handleSelectCategory}/>

              <SelectSubCat/>
              <div className={s.spaceLine}><br/></div>
              <SelectSubCat/>
            </form>
          </div>
        </div>
      </div>
  );
};

export default CreateAd;
