import {connect} from "react-redux";

import {changeLogin, changePassword} from "../../redux/LoginReducer";
import Login from "./Login";
import {setToggleForgotPassword, setToggleLogin, setToggleRegistration} from "../../redux/NavBarReducer";

let mapStateToProps = (state) => {
  return {
    login: state.auth.login,
    password: state.auth.password
  }
}

const LoginContainer = connect(mapStateToProps, {changeLogin, changePassword, setToggleLogin, setToggleRegistration, setToggleForgotPassword})(Login);

export default LoginContainer;
