import React, {useEffect} from 'react';

import s from './EditAdvert.module.css'
import Characteristics from "../CreateAd/Characteristics/Characteristics";
import {useParams} from "react-router-dom";
import MainCharacteristic from "../CreateAd/MainCharacteristic/MainCharacteristic";
import useLocalStorage from "../../hooks/useLocalStorage";

const EditAdvert = (props) => {

  const adId = parseInt(useParams().id);
  const [token] = useLocalStorage(null,'token');

  useEffect( () => {
    if (props.currentAdvert === null && token !== "null") {
      props.getCurrentAdvert(adId);
      props.getCurrentCharact(adId);
    }
  }, [props.currentAdvert]);

  useEffect(() => {
    if (props.tokenData === null && token !== "null")
      props.getTokenData(token);
  }, [props.tokenData]);

  console.log(props.currentAdvert)
  console.log(props.currentCharact)

  useEffect( () => {
    if (props.currentAdvert !== null && props.currentCharact) {
      debugger;

      props.changeHeader
      props.changeDescription
      props.changePrice

      props.changeBrand
      props.changeCapacity
      props.changeGears
      props.changeMilage
      props.changeMonthlyCost
      props.changeRelaseYear
      props.changeRent
      props.changeRentPeriod
      props.changeRoomNumber
      props.changeStage
      props.changeStages
      props.changeSubCategory
      props.changeWasInUse
      props.changeWheelDrive
    }
  }, [props.currentAdvert, props.currentCharact]);

  return (
      <div className={s.edit}>
        <h3 className={s.h3}>Редактирование объявления</h3>

          {token !== "null" && props.currentAdvert !== null && props.tokenData !== null &&
              (props.tokenData.user_name === props.currentAdvert.owner || props.tokenData.authorities[0] === "ADMIN") &&
              <div className={s.edit__container}>
                <form className={s.edit__form}>
                  <MainCharacteristic store={{
                    ...props,
                    header: props.currentAdvert.header,
                    description: props.currentAdvert.description,
                    price: props.currentAdvert.price
                  }}/>

                  <Characteristics updateAdvert={props.updateAdvert}
                                   updateCharact={props.updateCharact}
                                   store={{
                                     ...props,
                                     brand: '',
                                     capacity: props.currentCharact.capacity,
                                     milage: props.currentCharact.milage,
                                     relaseYear: props.currentCharact.relaseYear,
                                     rent: props.currentCharact.rent,
                                     rentPeriod: props.currentCharact.rentPeriod,
                                     roomNumber: props.currentCharact.roomNumber,
                                     stage: props.currentCharact.floor ? props.currentCharact.floor : props.currentCharact.floorCount,
                                     wasInUse: props.currentCharact.wasInUse,
                                     wheelDrive: props.currentCharact.wheelDrive,
                                     monthlyCost: props.currentCharact.monthlyCost,
                                     floor: props.currentCharact.floor,
                                     floorCount: props.currentCharact.floorCount
                                   }}
                                   type={{id: props.currentAdvert.subId}}/>
                </form>
              </div>
          }
          {token === "null" || (props.currentAdvert !== null && props.tokenData !== null &&
                  (props.tokenData.user_name !== props.currentAdvert.owner || props.tokenData.authorities[0] !== "ADMIN")) &&
              <span className={s.edit__error}>У Вас нет прав на редактирование данного объявления :(</span>
          }

      </div>
  );
};

export default EditAdvert;
