import React, {useState} from 'react';
import Input from "../Input/Input";
import OptionalSelector from "../OptionalSelector/OptionalSelector";
import SpaceLine from "../../SpaceLine";

import s from "./Characteristics.module.css";
import {NavLink} from "react-router-dom";

const Characteristics = (props) => {

  const [rent, setRent] = useState('Не выбрано');
  const handleSetRent = (event) => {
    if (event.target.value !== 'Не выбрано')
      setRentPeriod('Не выбрано');

    setRent(event.target.value);
  }

  let rentPeriod = props.store.rentPeriod;
  const onChangeRentPeriod = (event) => { props.store.changeRentPeriod(event.target.value); }

  let roomNumber = props.store.roomNumber;
  const onChangeRoomNumber = (event) => { props.store.changeRoomNumber(event.target.value); }

  let stage = props.store.stage;
  const onChangeStage = (event) => { props.store.changeStage(event.target.value); }

  let brand = props.store.brand;
  const onChangeBrand = (event) => { props.store.changeBrand(event.target.value); }

  let relaseYear = props.store.relaseYear;
  const onChangeRelaseYear = (event) => { props.store.changeRelaseYear(event.target.value); }

  let milage = props.store.milage;
  const onChangeMilage = (event) => { props.store.changeMilage(event.target.value); }

  let capacity = props.store.capacity;
  const onChangeCapacity = (event) => { props.store.changeCapacity(event.target.value); }

  let wheelDrive = props.store.wheelDrive;
  const onChangeWheelDrive = (event) => { props.store.changeWheelDrive(event.target.value); }

  let gears = props.store.gears;
  const onChangeGears = (event) => { props.store.changeGears(event.target.value); }

  let wasInUse = props.store.wasInUse;
  const onChangeWasInUse = (event) => { props.store.changeWasInUse(event.target.value); }

  let listItemsToOptions = (listItems) => {
    return listItems.map(el => <option key={el.id} value={el.option}>{el.option}</option>);
  };

  const [fileNameList, setFileNameList] = useState([]);
  let imgList;
  if (fileNameList.length > 0)
    imgList = <div>{fileNameList.toString()}</div>
  else
    imgList = <div>Файлы не выбраны!</div>

  const setImages = () => {
    let fileNameList = [];
    let fileInputList = document.querySelector("#imageUploads").files;
    for (let i = 0; i < fileInputList.length; i++) {
      fileNameList[i] = fileInputList.item(i).name;
    }
    setFileNameList(fileNameList);
  }

  return (
      <div className={s.characteristics}>
        <SpaceLine width={600}/>

        {props.type === 'Квартиры' && <div>
          <OptionalSelector label={'Аренда'} value={rent} handle={handleSetRent} options={listItemsToOptions(props.optional.bool)}/>
          {rent === 'Да' && <OptionalSelector label={'Срок аренды'} value={rentPeriod} handle={handleSetRentPeriod} options={listItemsToOptions(props.optional.rent)}/>}
          <Input name={'Количество комнат'} value={roomNumber} handle={handleSetRoomNumber}/>
          <Input name={'Этаж'} value={stage} handle={handleSetStage}/>
        </div>}
        {props.type === 'Дома' && <div>
          <OptionalSelector label={'Аренда'} value={rent} handle={handleSetRent} options={listItemsToOptions(props.optional.bool)}/>
          {rent === 'Да' && <OptionalSelector label={'Срок аренды'} value={rentPeriod} handle={handleSetRentPeriod} options={listItemsToOptions(props.optional.rent)}/>}
          <Input name={'Количество комнат'} value={roomNumber} handle={handleSetRoomNumber}/>
          <Input name={'Количество этажей'} value={stage} handle={handleSetStage}/>
        </div>}
        {props.type === 'Машины' && <div>
          <Input name={'Марка'} value={brand} handle={handleSetBrand}/>
          <Input name={'Год выпуска'} value={relaseYear} handle={handleSetRelaseYear}/>
          <Input name={'Пробег (км.)'} value={milage} handle={handleSetMilage}/>
          <Input name={'Объем двигателя'} value={capacity} handle={handleSetCapacity}/>
          <OptionalSelector label={'Привод'} value={wheelDrive} handle={handleSetWheelDrive} options={listItemsToOptions(props.optional.wheelDrive)}/>
        </div>}
        {props.type === 'Мотоциклы' && <div>
          <Input name={'Марка'} value={brand} handle={handleSetBrand}/>
          <Input name={'Год выпуска'} value={relaseYear} handle={handleSetRelaseYear}/>
          <Input name={'Пробег (км.)'} value={milage} handle={handleSetMilage}/>
          <Input name={'Объем двигателя'} value={capacity} handle={handleSetCapacity}/>
          <OptionalSelector label={'Количество передач'} value={gears} handle={handleSetGears} options={listItemsToOptions(props.optional.gears)}/>
        </div>}
        {props.type === 'Бытовая техника' && <div>
          <OptionalSelector label={'Б/у'} value={wasInUse} handle={handleSetWasInUse} options={listItemsToOptions(props.optional.bool)}/>
          <Input name={'Марка'} value={brand} handle={handleSetBrand}/>
          <Input name={'Год выпуска'} value={relaseYear} handle={handleSetRelaseYear}/>
        </div>}
        {props.type === 'Мебель' && <div>
          <OptionalSelector label={'Б/у'} value={wasInUse} handle={handleSetWasInUse} options={listItemsToOptions(props.optional.bool)}/>
          <Input name={'Марка'} value={brand} handle={handleSetBrand}/>
          <Input name={'Год выпуска'} value={relaseYear} handle={handleSetRelaseYear}/>
        </div>}
        {props.type !== 'Не выбрано' &&
            <span>
              <div className={s.uploadFiles}>
                <label className={s.uploadFiles__label}>Перетащите файл для загрузки, или <span className={s.uploadFiles__label_chose}>выберите</span></label>
                <input className={s.uploadFiles__input} id="imageUploads" type="file" accept={'.jpg, ,jpeg, .png'} multiple onChange={setImages}/>
              </div>
              <div className={s.listFiles}>
                {imgList}
              </div>
            </span>
        }
        <NavLink className={s.link} to={'/'}><button className={s.btn}>Создать</button></NavLink>
      </div>
  );
};

export default Characteristics;
