import React, {useState} from 'react';
import Input from "../Input/Input";
import OptionalSelector from "../OptionalSelector/OptionalSelector";
import SpaceLine from "../../SpaceLine";

import s from "./Characteristics.module.css";
import {NavLink} from "react-router-dom";

import trash from './trash.svg';
import Apartaments from "./Apartaments/Apartaments";

const Characteristics = (props) => {

  const [rent, setRent] = useState('Не выбрано');
  const handleSetRent = (event) => {
    if (event.target.value !== 'Не выбрано')
      setRentPeriod('Не выбрано');

    setRent(event.target.value);
  }

  const [rentPeriod, setRentPeriod] = useState('Не выбрано');
  const handleSetRentPeriod = (event) => { setRentPeriod(event.target.value); }

  const [roomNumber, setRoomNumber] = useState('');
  const handleSetRoomNumber = (event) => { setRoomNumber(event.target.value); }

  const [stage, setStage] = useState('');
  const handleSetStage = (event) => {setStage(event.target.value); }

  const [brand, setBrand] = useState('');
  const handleSetBrand = (event) => { setBrand(event.target.value); }

  const [relaseYear, setRelaseYear] = useState('');
  const handleSetRelaseYear = (event) => { setRelaseYear(event.target.value); }

  const [milage, setMilage] = useState('');
  const handleSetMilage = (event) => { setMilage(event.target.value); }

  const [capacity, setCapacity] = useState('');
  const handleSetCapacity = (event) => { setCapacity(event.target.value); }

  const [wheelDrive, setWheelDrive] = useState('Не выбрано');
  const handleSetWheelDrive = (event) => { setWheelDrive(event.target.value); }

  const [gears, setGears] = useState('Не выбрано');
  const handleSetGears = (event) => { setGears(event.target.value); }

  const [wasInUse, setWasInUse] = useState('Не выбрано');
  const handleSetWasInUse = (event) => { setWasInUse(event.target.value); }

  const optionalListBool = [{id: 0, option: 'Да'}, {id: 1, option: 'Нет'}]
  const optionalListRent = [{id: 0, option: 'Длительный'}, {id: 1, option: 'Посуточный'}]
  const optionalListWheelDrive = [{id: 0, option: 'Полный'}, {id: 1, option: 'Передний'}, {id: 2, option: 'Задний'}]
  const optionalListGears = [{id: 0, option: 1}, {id: 1, option: 2}, {id: 2, option: 3}, {id: 3, option: 4}, {id: 4, option: 5}, {id: 5, option: 6}]


  let listItemsToOptions = (listItems) => {
    return listItems.map(el => <option key={el.id} value={el.option}>{el.option}</option>);
  };

  const [fileNameList, setFileNameList] = useState([]);
  let imgList;
  if (fileNameList.length > 0)
    imgList = <div>{fileNameList.toString()}</div>
  else
    imgList = <div>Файлы не выбраны!</div>

  const setImages = () => {
    let fileNameList = [];
    let fileInputList = document.querySelector("#imageUploads").files;
    for (let i = 0; i < fileInputList.length; i++) {
      fileNameList[i] = fileInputList.item(i).name;
    }
    setFileNameList(fileNameList);
  }

  return (
      <div className={s.characteristics}>
        <SpaceLine/>

        {props.type === 'Квартиры' && <div>
          <Apartaments/>
        </div>}
        {props.type === 'Дома' && <div>
          <OptionalSelector label={'Аренда'} value={rent} handle={handleSetRent} options={listItemsToOptions(optionalListBool)}/>
          {rent === 'Да' && <OptionalSelector label={'Срок аренды'} value={rentPeriod} handle={handleSetRentPeriod} options={listItemsToOptions(optionalListRent)}/>}
          <Input name={'Количество комнат'} value={roomNumber} handle={handleSetRoomNumber}/>
          <Input name={'Количество этажей'} value={stage} handle={handleSetStage}/>
        </div>}
        {props.type === 'Машины' && <div>
          <Input name={'Марка'} value={brand} handle={handleSetBrand}/>
          <Input name={'Год выпуска'} value={relaseYear} handle={handleSetRelaseYear}/>
          <Input name={'Пробег (км.)'} value={milage} handle={handleSetMilage}/>
          <Input name={'Объем двигателя'} value={capacity} handle={handleSetCapacity}/>
          <OptionalSelector label={'Привод'} value={wheelDrive} handle={handleSetWheelDrive} options={listItemsToOptions(optionalListWheelDrive)}/>
        </div>}
        {props.type === 'Мотоциклы' && <div>
          <Input name={'Марка'} value={brand} handle={handleSetBrand}/>
          <Input name={'Год выпуска'} value={relaseYear} handle={handleSetRelaseYear}/>
          <Input name={'Пробег (км.)'} value={milage} handle={handleSetMilage}/>
          <Input name={'Объем двигателя'} value={capacity} handle={handleSetCapacity}/>
          <OptionalSelector label={'Количество передач'} value={gears} handle={handleSetGears} options={listItemsToOptions(optionalListGears)}/>
        </div>}
        {props.type === 'Бытовая техника' && <div>
          <OptionalSelector label={'Б/у'} value={wasInUse} handle={handleSetWasInUse} options={listItemsToOptions(optionalListBool)}/>
          <Input name={'Марка'} value={brand} handle={handleSetBrand}/>
          <Input name={'Год выпуска'} value={relaseYear} handle={handleSetRelaseYear}/>
        </div>}
        {props.type === 'Мебель' && <div>
          <OptionalSelector label={'Б/у'} value={wasInUse} handle={handleSetWasInUse} options={listItemsToOptions(optionalListBool)}/>
          <Input name={'Марка'} value={brand} handle={handleSetBrand}/>
          <Input name={'Год выпуска'} value={relaseYear} handle={handleSetRelaseYear}/>
        </div>}
        {props.type !== 'Не выбрано' &&
            <span>
              <div className={s.uploadFiles}>
                <label className={s.uploadFiles__label}>Перетащите файл для загрузки, или <span className={s.uploadFiles__label_chose}>выберите</span></label>
                <input className={s.uploadFiles__input} id="imageUploads" type="file" accept={'.jpg, ,jpeg, .png'} multiple onChange={setImages}/>
              </div>
              <div className={s.listFiles}>
                {imgList}
              </div>
            </span>
        }
        <NavLink className={s.link} to={'/'}><button className={s.btn}>Создать</button></NavLink>
      </div>
  );
};

export default Characteristics;
