import axios from "axios";

const SET_OAUTH_USER = 'SET_OAUTH_USER';
const SET_OAUTH_DATA = 'SET_OAUTH_DATA';

let initalState = {
  user: null
}

const OAuthReducer = (state = initalState, action) => {
  switch (action.type) {

    case SET_OAUTH_USER:
      return {
        ...state,
        user: action.user
      }

    case SET_OAUTH_DATA:


    default: return state;
  }
}

const setOAuthUser = (user) => ({ type: SET_OAUTH_USER, user })
export const getOAuthData = (location) => ({ type: SET_OAUTH_DATA, location })

export const getOAuthUser = (props) => {
  return (dispatch) => {
    axios.get(`https://api.vk.com/method/users.get?user_ids=${props.userId}&access_token=${props.accessToken}&v=5.131`, {
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Access-Control-Allow-Headers": "*",
        "Access-Control-Allow-Methods": "*",
        'Access-Control-Allow-Credentials': 'true',
        'Content-Type': 'application/json',
      }
    }).then(
        response => dispatch(setOAuthUser(response.data))
    )
  }
}

export default OAuthReducer;
