import axios from "axios";

const SET_IMMOVABLES = 'SET_IMMOVABLES';
const SET_TRANSPORT = 'SET_TRANSPORT';
const SET_HOUSEHOLD = 'SET_HOUSEHOLD';
const SET_HOUSES = 'SET_HOUSES';
const SET_APPARTAMENTS = 'SET_APPARTAMENTS';
const SET_CARS = 'SET_CARS';
const SET_MOTOCYCLES = 'SET_MOTOCYCLES';
const SET_APPLIANCE = 'SET_APPLIANCE';
const SET_FURNITURE = 'SET_FURNITURE';
const SET_NEW_ADS = 'SET_NEW_ADS';

let initialState = {

  // Категории
  immovables: [],
  transport: [],
  household: [],

  // Подкатегории
  houses: [],
  appartaments: [],

  cars: [],
  motocycles: [],

  appliances: [],
  furniture: [],

  // Последние добавленные
  newAds: []
}

const advertsReducer = (state = initialState, action) => {
  switch (action.type) {

    case SET_IMMOVABLES:
      return {
        ...state,
        immovables: action.ads
      }

    case SET_TRANSPORT:
      return {
        ...state,
        transport: action.ads
      }

    case SET_HOUSEHOLD:
      return {
        ...state,
        household: action.ads
      }

    case SET_HOUSES:
      return {
        ...state,
        houses: action.ads
      }

    case SET_APPARTAMENTS:
      return {
        ...state,
        appartaments: action.ads
      }

    case SET_CARS:
      return {
        ...state,
        cars: action.ads
      }

    case SET_MOTOCYCLES:
      return {
        ...state,
        motocycles: action.ads
      }

    case SET_APPLIANCE:
      return {
        ...state,
        appliances: action.ads
      }

    case SET_FURNITURE:
      return {
        ...state,
        furniture: action.ads
      }

    case SET_NEW_ADS:
      return {
        ...state,
        newAds: action.ads
      }


    default: return state;
  }
}

export const setImmovables = (ads) => ({ type: SET_IMMOVABLES, ads });
export const setTransport = (ads) => ({ type: SET_TRANSPORT, ads });
export const setHousehold = (ads) => ({ type: SET_HOUSEHOLD, ads });
export const setHouses = (ads) => ({ type: SET_HOUSES, ads });
export const setAppartaments = (ads) => ({ type: SET_APPARTAMENTS, ads });
export const setCars = (ads) => ({ type: SET_CARS, ads });
export const setMotocycles = (ads) => ({ type: SET_MOTOCYCLES, ads });
export const setAppliance = (ads) => ({ type: SET_APPLIANCE, ads });
export const setFurniture = (ads) => ({ type: SET_FURNITURE, ads });
export const setNewAds = (ads) => ({ type: SET_NEW_ADS, ads });

export const getImmovables = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/category/id1/?limit=10&page=${nextPage}`).then(
        response => dispatch(setImmovables(response.data))
    )
  }
}

export const getTransport = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/category/id2/?limit=10&page=${nextPage}`).then(
        response => dispatch(setTransport(response.data))
    )
  }
}

export const getHousehold = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/category/id3/?limit=10&page=${nextPage}`).then(
        response => dispatch(setHousehold(response.data))
    )
  }
}

export const getHouses = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id1/?limit=10&page=${nextPage}`).then(
        response => dispatch(setHouses(response.data))
    )
  }
}

export const getAppartaments = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id2/?limit=10&page=${nextPage}`).then(
        response => dispatch(setAppartaments(response.data))
    )
  }
}

export const getCars = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id3/?limit=10&page=${nextPage}`).then(
        response => dispatch(setCars(dispatch.data))
    )
  }
}

export const getMotocycles = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id4/?limit=10&page=${nextPage}`).then(
        response => dispatch(setMotocycles(response.data))
    )
  }
}

export const getAppliance = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id5/?limit=10&page=${nextPage}`).then(
        response => dispatch(setAppliance(response.data))
    )
  }
}

export const getFurniture = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id6/?limit=10&page=${nextPage}`).then(
        response => {
          if (response.data.length > 0)
            dispatch(setFurniture(response.data))
        }
    )
  }
}

export const getNewAds = (nextPage) => {
  return (dispatch) => {
    axios.get(`/ad/view?limit=10&page=${nextPage}`).then(
        response => {
          if (response.data.length > 0)
            dispatch(setNewAds(response.data))
        }
    )
  }
}

export default advertsReducer;
