import React from 'react';
import s from './SearchFilter.module.css';
import {getCategories, getSubCategories} from "../../../store/Actions/CreateAdActions";

const SearchFilter = (props) => {

  // props.currentCategory.name - запрашиваем category и subcategory
  // props.currentCategory.categoryName - запрашиваем subcategory
  // props.currentCategory.subcategories - запрашиваем сразу список характеристик
  if (props.currentCategory.name) {
    props.getSubCategories();
    props.getCategories();
  }

  return (
      <div className={s.filter__container}>

      </div>
  );
};

export default SearchFilter;
