import React, {useState} from 'react';
import s from './MyAdverts.module.css';
import SpaceLine from "../../SpaceLine";
import UserMenu from "../../UserMenu/UserMenu";
import Button from "../../UserMenu/Button/Button";

const MyAdverts = () => {

  let [avatar, setAvatar] = useState('/avatars/null.jpg');
  const localAvatar = localStorage.getItem('avatar');
  const localUsername = localStorage.getItem('username');
  const dateRegistration = "26.03.2022";

  if (localAvatar !== null && localAvatar !== "null")
    setAvatar(localAvatar);

  return (
      <div className={s.profile}>
        <div className={s.profile__container}>
          <div className={s.profile__info}>
            <img className={s.profile__info_avatar} src={avatar} alt="Logo"/>
            <span className={s.profile__info_username}>{localUsername}</span>
            <span className={s.profile__info_dateReg}>Дата регистрации: {dateRegistration}</span>
            <SpaceLine marginTop={15} height={2} width={250}/>
            <Button to={"/"} src={""} alt={""}/>
          </div>

          <div className={s.profile__adverts}>

          </div>
        </div>
      </div>
  );
};

export default MyAdverts;
