import {connect} from "react-redux";

import {changeLogin, changePassword, setAuth, getAuth, isAuth} from "../../redux/LoginReducer";
import Login from "./Login";
import {setToggleForgotPassword, setToggleLogin, setToggleRegistration} from "../../redux/NavBarReducer";

let mapStateToProps = (state) => {
  return {
    login: state.auth.login,
    password: state.auth.password,

    toggleLogin: state.navbar.toggleLogin,
    toggleRegistration: state.navbar.toggleRegistration,
    toggleForgorPassword: state.navbar.toggleForgorPassword,

    token: state.auth.token,
    refreshToken: state.auth.refreshToken,
  }
}

const LoginContainer = connect(mapStateToProps, {changeLogin, changePassword, setToggleLogin, setToggleRegistration, setToggleForgotPassword, getAuth})(Login);

export default LoginContainer;
