import axios from "axios";

/*export const MAIN_PAGE = {url: '/', name: 'Новые объявления'};

export const TRANSPORT = {url: '/transport', name: 'Транспорт'};
export const IMMOVABLES = {url: '/nedvijimost', name: 'Недвижимость'};
export const HOUSEHOLD = {url: '/vse_dlya_doma', name: 'Всё для дома'};

export const APPARTAMENTS = {url: '/nedvijimost/kvartiri', name: 'Квартиры'};
export const HOUSES = {url: '/nedvijimost/doma', name: 'Дома'};

export const CARS = {url: '/transport/mashini', name: 'Машины'};
export const MOTO = {url: '/transport/motocicly', name: 'Мотоциклы'};

export const APPLIANCE = {url: '/vse_dlya_doma/bitovaya_technika', name: 'Бытовая техника'};
export const FURNITURE = {url: '/vse_dlya_doma/mebel', name: 'Мебель'};

