import axios from "axios";
import {useLocation} from "react-router-dom";

const SET_ADVERTS = 'SET_ADVERTS';
const SET_LOCATION = 'SET_LOCATION';

const MAIN_PAGE = {url: '/', name: 'Новые объявления'};

const TRANSPORT = {url: '/transport', name: 'Транспорт'};

let initialState = {

  adverts: [],
  namePage: ''

}


const advertsReducer = (state = initialState, action) => {

  debugger;
  switch (action.type) {

    case SET_ADVERTS:
      let newAdvertList = [...state.adverts];
      action.ads.map(ad => {
        newAdvertList = [...newAdvertList, ad]
      });

      return {
        ...state,
        adverts: newAdvertList,
      }


    case SET_LOCATION:

      let locationName = '';
      switch (action.location) {

        case MAIN_PAGE.url:
          locationName = MAIN_PAGE.name;
          break;

        case TRANSPORT.url:
          locationName = TRANSPORT.name;
          break;

        case '/nedvijimost':
          locationName = 'Недвижимость';
          break;

        case '/all_for_home':
          locationName = 'Всё для дома';
          break;

        case '/nedvijimost/kvartiri':
          locationName = 'Квартиры';
          break;

        case '/nedvijimost/doma':
          locationName = 'Дома';
          break;

        case '/transport/mashini':
          locationName = 'Машины';
          break;

        case '/transport/motocicly':
          locationName = 'Мотоциклы';
          break;

        case '/all_for_home/bitovaya_technika':
          locationName = 'Бытовая техника';
          break;

        case '/all_for_home/mebel':
          locationName = 'Мебель';
          break;

        default: locationName = 'Новые объявления';
      }

      return {
        ...state,
        namePage: locationName
      }

    default: return state;
  }
}

export const setAdverts = (ads) => ({type: SET_ADVERTS, ads});

export const getImmovables = (page) => {
  return (dispatch) => {
    axios.get(`/ad/category/id1/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getTransport = (page) => {
  return (dispatch) => {
    axios.get(`/ad/category/id2/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getHousehold = (page) => {
  return (dispatch) => {
    axios.get(`/ad/category/id3/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getHouses = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id1/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getAppartaments = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id2/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getCars = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id3/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getMotocycles = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id4/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getAppliance = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id5/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getFurniture = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id6/?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const getNewAds = (page) => {
  return (dispatch) => {
    axios.get(`/ad/view?limit=8&page=${page}`).then(
        response => dispatch(setAdverts(response.data))
    )
  }
}

export const setNamePage = (location) => (
    { type: 'УСТАНОВКА ЛОКАЦИИ', location }

);

export const getAdverts = (props) => {
  return (dispatch) => {

    switch (props.location) {
      case '/':
        dispatch(getNewAds(props.page));
        break;

      case '/nedvijimost':
        dispatch(getImmovables(props.page));
        break;

      case '/transport':
        dispatch(getTransport(props.page));
        break;

      case '/all_for_home':
        dispatch(getHousehold(props.page));
        break;

      case '/nedvijimost/kvartiri':
        dispatch(getAppartaments(props.page));
        break;

      case '/nedvijimost/doma':
        dispatch(getHouses(props.page));
        break;

      case '/transport/mashini':
        dispatch(getCars(props.page));
        break;

      case '/transport/motocicly':
        dispatch(getMotocycles(props.page));
        break;

      case '/all_for_home/bitovaya_technika':
        dispatch(getAppliance(props.page));
        break;

      case '/all_for_home/mebel':
        dispatch(getFurniture(props.page));
        break;

      default:
        return null;
    }

  }
}

export default advertsReducer;
