import React from 'react';
import {useLocation} from "react-router-dom";
import axios from "axios";
import {getOAuthData} from "../../redux/OAuthReducer";

const OAuth = () => {
  const location = useLocation().hash;

  let accessToken;
  let expiresIn;
  let userId;
  let email;

  const handleOAuth = (props) => {
    let dataToken = location.match(/\w+=\w+/mg);

    if (location) {
      accessToken = dataToken[0].match(/=\w+/)[0].slice(1);
      expiresIn = dataToken[1].match(/=\w+/)[0].slice(1);
      userId = dataToken[2].match(/=\w+/)[0].slice(1);
      email = location.match(/\w+@\w+.\w+/)[0];
      if (accessToken)
        props.getOAuthData(location);
    }

  }

  return (
      <div>
        {handleOAuth()}
      </div>
  );
};

export default OAuth;
