import React from 'react';

import s from './Advert.module.css';
import {NavLink} from "react-router-dom";

const Advert = (props) => {

  //adId, description, header, owner, price, subId, createdAd, lastUpdatedAt, photos[]
  return (
      /*<NavLink to={`/ad/id${props.adId}`} className={s.navLinkTo}>
        <div className={s.container} style={{backgroundImage: `url("./${props.imgPath}")`}}>
          <span className={s.nameAd}>{props.adName}</span>

          <span className={s.price}>{props.price}$</span>
          <span className={s.description}>{props.description}</span>
          <span className={s.datePublish}>{props.datePublish}</span>
        </div>
      </NavLink>*/

      <NavLink to={`/ad/id${props.adId}`} className={s.navLinkTo}>
        <div className={s.container}>
          <img className={s.img} src={props.imgPath} alt=""/>
          <span className={s.nameAd}>{props.adName}</span>

          <span className={s.price}>{props.price}$</span>
          <span className={s.description}>{props.description}</span>
          <span className={s.datePublish}>{props.datePublish}</span>
        </div>
      </NavLink>
  );
};

export default Advert;
