import axios from "axios";

const CHANGE_LOGIN = 'CHANGE_LOGIN';
const CHANGE_PASSWORD = 'CHANGE_PASSWORD';
const SET_AUTH = 'SET_AUTH';
const IS_AUTH = 'IS_AUTH';
const SET_USER = 'SET_USER';
const SET_LOGOUT = 'SET_LOGOUT';

let initialState = {
  login: '',
  password: '',
  token: '',
  refreshToken: '',
  isAuth: false,
  user: null
}

const loginReducer = (state = initialState, action) => {

  switch (action.type) {
    case CHANGE_LOGIN:
      return {
        ...state,
        login: action.login,
      }

    case CHANGE_PASSWORD:
      return {
        ...state,
        password: action.password,
      }

    case SET_AUTH:
      return {
        ...state,
        token: action.data.access_token,
        refreshToken: action.data.refresh_token,
        isAuth: true
      }

    case IS_AUTH:
      return {
        ...state,
        isAuth: action.isAuth
      }

    case SET_USER:
      debugger;
      return {
        ...state,
        user: action.data
      }

    case SET_LOGOUT:
      return initialState;

    default:
      return state;
  }
}

export const changeLogin = (login) => ({ type: CHANGE_LOGIN, login });
export const changePassword = (password) => ({ type: CHANGE_PASSWORD, password });
export const setAuth = (data) => ({type: SET_AUTH, data});
export const setIsAuth = (isAuth) => ({ type: IS_AUTH, isAuth })
export const setUser = (data) => ({ type: SET_USER, data })
export const setLogout = () => ({ type: SET_LOGOUT });

export const checkIsAuth = (token) => {
  return (dispatch) => {
    axios.post(`http://localhost:8080/oauth/check_token?token=${token}`).then(
        dispatch(setIsAuth(true))
    ).catch(
        dispatch(setLogout)
    )
  }
}

export const getAuth = (props) => {
  return (dispatch) => {
    axios.post('http://localhost:8080/oauth/token', `grant_type=password&scope=ad&username=${props.login}&password=${props.password}&client_id=ad-client&client_secret=zxc`, {
      headers: {
        'Access-Control-Allow-Origin': 'http://localhost:3000'
      }
    }).then(
        response => {
          dispatch(setAuth(response.data));
        }
    );
  }
}

export const getUser = (props) => {
  return (dispatch) => {
    axios.get(`http://localhost:8080/user/${props.login}`, {
      headers: {
        'Authorization': `bearer ${props.token}`
      }
    }).then(
        response => {
          dispatch(setUser(response.data))
        }
    )
  }
}

export default loginReducer;
