import React from 'react';
import {useLocation} from "react-router-dom";
import axios from "axios";

const OAuth = () => {
  const location = useLocation().hash;

  const handleOAuth = () => {
    let dataToken = location.match(/\w+=\w+/mg);

    const token = dataToken[0].match(/=\w+/)[0].slice(1);
    const expiresIn = dataToken[1].match(/=\w+/)[0].slice(1);
    const userId = dataToken[2].match(/=\w+/)[0].slice(1);
    const email = dataToken[3].match(/=\w+/)[0].slice(1);

    debugger;

    axios.post(`/oauth/check_token?token=${token}`).then(
        response => console.log(response)
    )
  }

  return (handleOAuth());
};

export default OAuth;
