import axios from "axios";

const CHANGE_LOGIN = 'CHANGE_LOGIN';
const CHANGE_PASSWORD = 'CHANGE_PASSWORD';
const SET_AUTH = 'SET_AUTH';

let initialState = {
  login: '',
  password: '',
  token: '',
  refreshToken: ''
}

const loginReducer = (state = initialState, action) => {
  debugger;

  switch (action.type) {
    case CHANGE_LOGIN:
      return {
        ...state,
        login: action.login,
      }

    case CHANGE_PASSWORD:
      return {
        ...state,
        password: action.password,
      }

    case SET_AUTH:
      if (state.login !== '' && state.password !== '') {
        axios.post('/oauth/token', `grant_type=password&scope=ad&username=${state.login}&password=${state.password}`).then(
          res => {
            state.token = res.data.access_token;
            state.refreshToken = res.data.refresh_token;
          }
        );
      }
      return {...state}

    default:
      return state;
  }
}

export const changeLogin = (login) => ({ type: CHANGE_LOGIN, login });
export const changePassword = (password) => ({ type: CHANGE_PASSWORD, password });
export const setAuth = () => ({type: SET_AUTH, data});

export const getAuth = () => {
  return (dispatch) => {
    axios.post('/oauth/token', `grant_type=password&scope=ad&username=${state.login}&password=${state.password}`).then(
        res => {
          dispatch(setAuth(res.data));
        }
    );
  }
}

export default loginReducer;
