const GET_CURRENT_SUB_ID = 'GET_CURRENT_SUB_ID';

let initialState = {
  currentAdvert: 0
}

const editAdvertReducer = (state = initialState, action) => {
  switch (action.type) {

    case GET_CURRENT_SUB_ID: {}
      return {
        ...state,
        currentAdvert: action.advert
      }

    default: return state;
  }
}

export const setCurrentAdvert = (advert) => ({ type: GET_CURRENT_SUB_ID, advert });

export default editAdvertReducer;
