import axios from "axios";

const SET_IMMOVABLES = 'SET_IMMOVABLES';
const SET_TRANSPORT = 'SET_TRANSPORT';
const SET_HOUSEHOLD = 'SET_HOUSEHOLD';
const SET_HOUSES = 'SET_HOUSES';
const SET_APPARTAMENTS = 'SET_APPARTAMENTS';
const SET_CARS = 'SET_CARS';
const SET_MOTOCYCLES = 'SET_MOTOCYCLES';
const SET_APPLIANCE = 'SET_APPLIANCE';
const SET_FURNITURE = 'SET_FURNITURE';
const SET_NEW_ADS = 'SET_NEW_ADS';

let initialState = {

  // Категории
  immovables: [],
  transport: [],
  household: [],

  // Подкатегории
  houses: [],
  appartaments: [],

  cars: [],
  motocycles: [],

  appliances: [],
  furniture: [],

  // Последние добавленные
  newAds: [],

  hasMore: false,
  prevLocation: ''
}

const advertsReducer = (state = initialState, action) => {
  switch (action.type) {

    case SET_IMMOVABLES:
      return {
        ...state,
        immovables: [...state.immovables, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_TRANSPORT:
      return {
        ...state,
        transport: [...state.transport, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_HOUSEHOLD:
      return {
        ...state,
        household: [...state.household, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_HOUSES:
      return {
        ...state,
        houses: [...state.houses, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_APPARTAMENTS:
      return {
        ...state,
        appartaments: [...state.appartaments, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_CARS:
      return {
        ...state,
        cars: [...state.cars, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_MOTOCYCLES:
      return {
        ...state,
        motocycles: [...state.motocycles, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_APPLIANCE:
      return {
        ...state,
        appliances: [...state.appliances, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_FURNITURE:
      return {
        ...state,
        furniture: [...state.furniture, action.ads],
        hasMore: action.ads.length > 0
      }

    case SET_NEW_ADS:
      return {
        ...state,
        newAds: [...state.newAds, action.ads],
        hasMore: action.ads.length > 0
      }


    default: return state;
  }
}

export const setImmovables = (ads) => ({ type: SET_IMMOVABLES, ads });
export const setTransport = (ads) => ({ type: SET_TRANSPORT, ads });
export const setHousehold = (ads) => ({ type: SET_HOUSEHOLD, ads });
export const setHouses = (ads) => ({ type: SET_HOUSES, ads });
export const setAppartaments = (ads) => ({ type: SET_APPARTAMENTS, ads });
export const setCars = (ads) => ({ type: SET_CARS, ads });
export const setMotocycles = (ads) => ({ type: SET_MOTOCYCLES, ads });
export const setAppliance = (ads) => ({ type: SET_APPLIANCE, ads });
export const setFurniture = (ads) => ({ type: SET_FURNITURE, ads });
export const setNewAds = (ads) => ({ type: SET_NEW_ADS, ads });

export const getImmovables = (page) => {
  return (dispatch) => {
    axios.get(`/ad/category/id1/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setImmovables(ad)))
    )
  }
}

export const getTransport = (page) => {
  return (dispatch) => {
    axios.get(`/ad/category/id2/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setTransport(ad)))
    )
  }
}

export const getHousehold = (page) => {
  return (dispatch) => {
    axios.get(`/ad/category/id3/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setHousehold(ad)))
    )
  }
}

export const getHouses = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id1/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setHouses(ad)))
    )
  }
}

export const getAppartaments = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id2/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setAppartaments(ad)))
    )
  }
}

export const getCars = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id3/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setCars(ad)))
    )
  }
}

export const getMotocycles = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id4/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setMotocycles(ad)))
    )
  }
}

export const getAppliance = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id5/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setAppliance(ad)))
    )
  }
}

export const getFurniture = (page) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id6/?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setFurniture(ad)))
    )
  }
}

export const getNewAds = (page) => {
  return (dispatch) => {
    axios.get(`/ad/view?limit=10&page=${page}`).then(
        response => response.data.map(ad => dispatch(setNewAds(ad)))
    )
  }
}

export const getAdverts = (props) => {
  return (dispatch) => {
    if (state.prevLocation !== props.location)
      adverts: []

    state.prevLocation = props.location;

    switch (props.location) {
      case '/':
        dispatch(getNewAds(props.page));
        break;

      case '/nedvijimost':
        dispatch(getImmovables(props.page));
        break;

      case '/transport':
        dispatch(getTransport(props.page));
        break;

      default:
        return null;
    }

  }
}

export default advertsReducer;
