import axios from "axios";
import {setCategories, setSubCategories} from "../Reducers/CreateAdReducer";

export const getCategories = () => {
  return (dispatch) => {
    axios.get('http://localhost:8080/category/').then(
        categories => {
          categories.data.map(category => {
            dispatch(setCategories(category));
          })
        }
    );
  }
};

export const getSubCategories = () => {
  return (dispatch) => {
    axios.get('http://localhost:8080/subcategory/').then(
        subcategories => dispatch(setSubCategories(subcategories))
    );
  }
};
