import React, {useEffect} from 'react';
import {useParams} from "react-router-dom";

const ConfirmAccount = (props) => {

  const confirmCode = useParams().code;

  useEffect( () => {
    if (props.user === null)
      props.getActivationCode(confirmCode);
  }, props.user);

  debugger;

  return (
      <div>
        {confirmCode}
      </div>
  );
};

export default ConfirmAccount;
