import axios from "axios";

const SET_USER = 'SET_USER';
const SET_ADVERTS = 'SET_ADVERTS';

let initialState = {
  user: {},
  adverts: []
}

const myAdvertsReducer = (state = initialState, action) => {
  switch (action.type) {

    case SET_USER:
      return {
        ...state,
        user: action.user
      }

    case SET_ADVERTS:
      return {
        ...state,
        adverts: action.adverts
      }
    default:
      return state;
  }
}

export const setUser = (user) => ({ type: SET_USER, user });
export const setAdverts = (adverts) => ({ type: SET_ADVERTS, adverts });

export const getUser = (props) => {
  return (dispatch) => {
    axios.get(`/user/${props.user}`, {
      headers: {
        'Authorization': `bearer ${props.token}`
      }
    }).then(
        response => {
          dispatch(setUser(response.data));
        }
    )
  }
}

export const getAdverts = (props) => {
  return (dispatch) => {
    axios.get(`/ad/user/${props.username}`).then(
        response => {
          dispatch(setAdverts(response.data));
        }
    )
  }
}

export default myAdvertsReducer;
