import React, {useState} from 'react';

import s from './Registration.module.css';
import AuthInput from "../AuthInput/AuthInput";
import SpaceLine from "../../SpaceLine";

import gmail from "../Login/gmail.svg";
import facebook from "../Login/facebook.svg";
import vk from "../Login/vk.svg";

const Registration = (props) => {
  const [login, setLogin] = useState('');
  const handleSetLogin = (event) => {
    setLogin(event.target.value);
  }
  const [mail, setMail] = useState('');
  const handleSetMail = (event) => { setMail(event.target.value); }

  const [number, setNumber] = useState('');
  const handleSetNumber = (event) => { setNumber(event.target.value); }

  const [password, setPassword] = useState('');
  const handleSetPassword = (event) => { setPassword(event.target.value); }

  const [repeatPassword, setRepeatPassword] = useState('');
  const handleSetRepeatPassword = (event) => { setRepeatPassword(event.target.value); }

  return (
      <div className={s.reg}>
        <div className={s.reg__background} onClick={props.handle}/>
        <div className={s.reg__window}>
          <div className={s.reg__content}>
            <AuthInput name={'Логин'} placeholder={'Придумайте Ваш логин'} value={login} handle={handleSetLogin}/>
            <AuthInput name={'Адрес электронной почты'} placeholder={'example@exmpl.ru'} value={mail} handle={handleSetMail}/>
            <AuthInput name={'Номер телефона'} placeholder={'+7 (111) 222-33-44'} value={number} handle={handleSetNumber}/>
            <SpaceLine width={300}/>
            <AuthInput name={'Пароль'} placeholder={'Введите пароль'} value={password} handle={handleSetPassword}/>
            <AuthInput name={'Повторите пароль'} placeholder={'Повторите пароль'} type={'password'} value={repeatPassword} handle={handleSetRepeatPassword}/>
            {password !== repeatPassword && repeatPassword !== '' && <div className={s.reg__error}>Пароли должны совпадать!</div>}
            <SpaceLine width={300}/>
            <button className={s.reg_btn}>Зарегистрироваться</button>

            <SpaceLine width={300} marginBottom={0}/>

            <span className={s.fastRegistration}>Быстрая регистрация</span>

            <div className={s.reg__fastRegistration}>
              <img className={s.reg__fastRegistration_item} src={gmail} alt="gmail"/>
              <img className={s.reg__fastRegistration_item} src={facebook} alt="facebook"/>
              <img className={s.reg__fastRegistration_item} src={vk} alt="vk"/>
            </div>
          </div>
        </div>
      </div>
  );
};

export default Registration;
